mapper.select:
SELECT 
		COUNT(*)
FROM 
		MPBATCH_CHECKACCOUNT_STATUS_INF

	 1 = 1 
        
				AND BANKCODE =  #{bankcode}
		
        
				AND CHKTYPE =  #{chktype}
		
        
				AND ACTDATE =  #{actdate}
		
        
				AND BANKFILSTS =  #{bankfilsts}
		
        
				AND BKGFILSTS =  #{bkgfilsts}
		
        
				AND CHKPROSTS =  #{chkprosts}

mapper.insert:
INSERT INTO MPBATCH_VERIFIACCOUNT_THD_INF ( BK_CODE, MER_NO, TER_NO, TRAN_TIME, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, CK_DT ) SELECT BK_CODE, MER_NO, TER_NO, TRAN_TIME, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, CK_DT FROM MPBATCH_VERIFIACCOUNT_DOUBT_INF
CK_DOUBTTYPE = '01'
AND CK_DT = #{actdate}

INSERT INTO MPBATCH_VERIFIACCOUNT_PAY_INF ( BK_CODE, MER_NO, TER_NO, TRAN_TIME, LOC_PAY_NO, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, CK_DT ) SELECT BK_CODE, MER_NO, TER_NO, TRAN_TIME, LOC_PAY_NO, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, CK_DT FROM MPBATCH_VERIFIACCOUNT_DOUBT_INF
CK_DOUBTTYPE = '02'
AND CK_DT = #{actdate}

INSERT INTO MPBATCH_VERIFIACCOUNT_PAY_INF ( BK_CODE, MER_NO, TER_NO, TRAN_TIME, LOC_PAY_NO, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, CK_DT ) SELECT #{bankcode}, JOURNAL.TMERCID, JOURNAL.TTERMID, concat(JOURNAL.TXN_DATE,JOURNAL.TXN_TIME), JOURNAL.PAYORDNO, JOURNAL.TLOGNO, JOURNAL.TXN_ACTNO, '', JOURNAL.TXN_AMT, JOURNAL.TXN_AMT-TXN_FEE, '', JOURNAL.TSREFNO, '1', #{actdate} FROM MPOMNG_PAYMENT_JOURNAL_INF JOURNAL LEFT JOIN MPOMNG_PAY_INF INF ON JOURNAL.PAYORDNO=INF.PAYORDNO
JOURNAL.TXN_STATUS = 'S' AND INF.PAY_CHANNEL = 'STHDHX'
AND JOURNAL.TXN_DATE = #{actdate}

INSERT INTO MPBATCH_VERIFIACCOUNT_PAY_INF ( BK_CODE, MER_NO, TER_NO, TRAN_TIME, LOC_PAY_NO, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, CK_DT ) SELECT #{bankcode}, TMERCID, TTERMID, concat(TXN_DATE,TXN_TIME), PAYORDNO, TLOGNO, TXN_ACTNO, '', TXN_AMT, TXN_AMT-TXN_FEE, '', TSREFNO, '2', #{actdate} FROM MPOMNG_ANOTHERPAY_JOURNAL_INF
TXN_STATUS = '01'
AND TXN_DATE = #{actdate}

mapper.delete:
DELETE FROM MPBATCH_CHECKACCOUNT_DOUBT_INF

	
				AND BANKCODE =  #{bankcode}
	
    
				AND ACTDATE =  #{actdate}

mapper.insert:
INSERT INTO MPBATCH_VERIFIACCOUNT_DOUBT_INF ( BK_CODE, MER_NO, TER_NO, TRAN_TIME, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, LOC_PAY_NO, CK_DOUBTTYPE, CK_NUM, CK_DT ) SELECT MP.BK_CODE, MP.MER_NO, MP.TER_NO, MP.TRAN_TIME, MP.TRAN_SEQ, MP.CARD_NO, MP.PAY_NO, MP.PAY_AMT, MP.INC_AMT, MP.SETTLE_DT, MP.REF_NO, MP.TRAN_TYPE, MP.LOC_PAY_NO, '02', '1', MP.CK_DT FROM MPBATCH_VERIFIACCOUNT_PAY_INF MP
NOT EXISTS (SELECT BK.TRAN_SEQ FROM MPBATCH_VERIFIACCOUNT_THD_INF BK WHERE MP.TRAN_SEQ=BK.TRAN_SEQ)
AND MP.BK_CODE = #{bankcode}
AND MP.CK_DT = #{actdate}

INSERT INTO MPBATCH_VERIFIACCOUNT_DOUBT_INF ( BK_CODE, MER_NO, TER_NO, TRAN_TIME, TRAN_SEQ, CARD_NO, PAY_NO, PAY_AMT, INC_AMT, SETTLE_DT, REF_NO, TRAN_TYPE, CK_DOUBTTYPE, CK_NUM, CK_DT ) SELECT BK.BK_CODE, BK.MER_NO, BK.TER_NO, BK.TRAN_TIME, BK.TRAN_SEQ, BK.CARD_NO, BK.PAY_NO, BK.PAY_AMT, BK.INC_AMT, BK.SETTLE_DT, BK.REF_NO, BK.TRAN_TYPE, '01', '1', BK.CK_DT FROM MPBATCH_VERIFIACCOUNT_THD_INF BK
NOT EXISTS (SELECT MP.TRAN_SEQ FROM MPBATCH_VERIFIACCOUNT_PAY_INF MP WHERE MP.TRAN_SEQ=BK.TRAN_SEQ)
AND BK.BK_CODE = #{bankcode}
AND BK.CK_DT = #{actdate}

mapper.update:
UPDATE 
		MPBATCH_CHECKACCOUNT_DOUBT_INF
		SET CHECKNUM = (CASE WHEN CHECKNUM IS NULL THEN '1' ELSE '2' END)

	
				AND BANKCODE =  #{bankcode}
	
    
				AND ACTDATE =  #{actdate}

mapper.insert:
INSERT INTO MPBATCH_CHECKACCOUNT_ERROR_INF ( BANKCODE, ACTDATE, BANKLOGNO, CHKTYPE, BANKDATE, CCYCOD, TXNAMT, FEE, INAMT, CUST_ID, PAYNO, CHECKDATE, CHECKTIME, CHECKDOUBTTYPE, CHECKNUM ) SELECT MP.BANKCODE, MP.ACTDATE, MP.BANKLOGNO, MP.CHKTYPE, MP.BANKDATE, MP.CCYCOD, MP.TXNAMT, MP.FEE, MP.INAMT, MP.CUST_ID, MP.PAYNO, MP.CHECKDATE, MP.CHECKTIME, MP.CHECKDOUBTTYPE, MP.CHECKNUM FROM MPBATCH_CHECKACCOUNT_DOUBT_INF MP
MP.CHECKNUM = '2'
AND MP.BANKCODE = #{bankcode}
AND MP.ACTDATE = #{actdate}

mapper.delete:
		DELETE FROM MPBATCH_CHECKACCOUNT_DOUBT_INF
          
            CHECKNUM = '2'
			
						AND  BANKCODE =  #{bankcode}
			
		    
						AND  ACTDATE =  #{actdate}

mapper.select:
SELECT 
         BANKCODE, 
         ACTDATE, 
         BANKLOGNO, 
         CHKTYPE, 
         BANKDATE, 
         CCYCOD, 
         TXNAMT, 
         FEE, 
         INAMT, 
         CUST_ID, 
         PAYNO, 
         CHECKDATE, 
         CHECKTIME, 
         CHECKTYPE, 
         CHECKNUM, 
FROM 
        MPBATCH_CHECKACCOUNT_DOUBT_INF

         1 = 1 
        
				AND BANKCODE =  #{bankcode}
		
        
				AND ACTDATE =  #{actdate}
		
        
				AND BANKLOGNO =  #{banklogno}
		
        
				AND CHKTYPE =  #{chktype}
		
        
				AND BANKDATE =  #{bankdate}
		
        
				AND CCYCOD =  #{ccycod}
		
        
				AND TXNAMT =  #{txnamt}
		
        
				AND FEE =  #{fee}
		
        
				AND INAMT =  #{inamt}
		
        
				AND CUST_ID =  #{custId}
		
        
				AND PAYNO =  #{payno}
		
        
				AND CHECKDATE =  #{checkdate}
		
        
				AND CHECKTIME =  #{checktime}
		
        
				AND CHECKTYPE =  #{checktype}
		
        
				AND CHECKNUM =  #{checknum}

SELECT 
         BANKCODE, 
         ACTDATE, 
         BANKLOGNO, 
         CHKTYPE, 
         BANKDATE, 
         CCYCOD, 
         TXNAMT, 
         FEE, 
         INAMT, 
         CUST_ID, 
         PAYNO, 
         CHECKDATE, 
         CHECKTIME, 
         CHECKTYPE, 
         CHECKNUM, 
FROM 
       MPBATCH_CHECKACCOUNT_DOUBT_INF

    1 = 1 
        
				AND BANKCODE =  #{bankcode}
		
        
				AND ACTDATE =  #{actdate}
		
        
				AND BANKLOGNO =  #{banklogno}
		
        
				AND CHKTYPE =  #{chktype}
		
        
				AND BANKDATE =  #{bankdate}
		
        
				AND CCYCOD =  #{ccycod}
		
        
				AND TXNAMT =  #{txnamt}
		
        
				AND FEE =  #{fee}
		
        
				AND INAMT =  #{inamt}
		
        
				AND CUST_ID =  #{custId}
		
        
				AND PAYNO =  #{payno}
		
        
				AND CHECKDATE =  #{checkdate}
		
        
				AND CHECKTIME =  #{checktime}
		
        
				AND CHECKTYPE =  #{checktype}
		
        
				AND CHECKNUM =  #{checknum}
		

ORDER BY  DESC LIMIT #{start},#{pageSize}

SELECT 
		COUNT(*)
FROM 
		MPBATCH_CHECKACCOUNT_DOUBT_INF

	 1 = 1 
        
				AND BANKCODE =  #{bankcode}
		
        
				AND ACTDATE =  #{actdate}
		
        
