				AND CHKTYPE =  #{chktype}
		
        
				AND BANKDATE =  #{bankdate}
		
        
				AND CCYCOD =  #{ccycod}
		
        
				AND TXNAMT =  #{txnamt}
		
        
				AND FEE =  #{fee}
		
        
				AND INAMT =  #{inamt}
		
        
				AND CUST_ID =  #{custId}
		
        
				AND PAYNO =  #{payno}
		
        
				AND CHECKDATE =  #{checkdate}
		
        
